INSERT INTO front_page_products(name, description, category_id,  price, on_sale) VALUES('Nintendo 64',  'Nintendo 64 er þriðja kynslóðin af leikjatölvu frá Nintendo. Leikjatölvan var fyrst kynnt árið 1996. Nintendo 64 er nefnd eftir 64 - bita örgjörvanum sem tölvan inniheldur.', 1, 25000, false); 
INSERT INTO front_page_products(name, description, category_id,  price, on_sale) VALUES('GameBoy Original', 'Game boy er 8-bita leikajtölva sem er þróuð og framleidd af fyrirtækinu Nintendo. Leikjatölvan var fyrst gefin út árið 1989 í japan.', 2, 10000, false);
INSERT INTO front_page_products(name, description, category_id,  price, on_sale) VALUES('PlayStation 1', 'PlayStation er heima leikjatölva sem er þróuð og framleidd af Sony. Leikjatölvan var fyrst gefin út árið 1994 í japan. PlayStation tölvan varð mikill keppinautur við Nintendo 64 leikjatölvuna.',3,8000, false); 
INSERT INTO front_page_products(name, description, category_id,  price, on_sale) VALUES('Xbox 360', 'Xbox 360 er miklu meira en bara leikjatölva. Þessi tölva gerir notendum kleift að spila, streyma og hlaða niður allskyns miðlum. T.d. er hægt að nálgast háskperukvikmyndir, tónlist, stfrænar myndir og leiki. Xbox 360 var fyrst gefin úr árið 2005 og er þróað af Microsoft.', 4, 18000, false);
INSERT INTO front_page_products(name, price, category_id, on_sale, description) VALUES('Mario kart 64',10000,  1, false, 'Mario Kart 64 er meira en venjulegur kappakstursleikur. Þetta er bardagi á hjólum. Leikurinn inniheldur 16 mismunandi kappaksturs brautir í 3D sem ná yfir allan Mario heiminn.');
INSERT INTO front_page_products(name, price, category_id, on_sale, description) VALUES('Pokemon Stadium',7000,  1, false, 'Að spila þennan leik lætur þér líða eins og alvöru Pokemon meistara! Leikurinn var gefinn út af Nintendo fyrir Nintendo 64 leikjatölvuna árið 1999. Pokémon Stadium varð einn af mest seldu titlum Nintendo 64 en milljón eintök seldust fyrir árslok 2000.');
INSERT INTO front_page_products(name, price, category_id, on_sale, description) VALUES('Legend of Zelda: Ocarina of Time',11000,  1, false, 'Við útgáfu á leiknum var hann tilkynntur sem besti leikur allra tíma. Ógnvekjandi Z-miðlunarkerfi leiksins gæti umbreytt hvaða bardaga sem er í kvikmyndalegt einvígi. Hasar-ævintýraleikur þar sem spilarinn stjórnar persónunni Link í fantasíulandinu Hyrule í leit að stöðva vonda konunginn Ganondorf. Tónlist gegnir mikilvægu hlutverki, þar sem spilarinn verður að læra að spila fjölmörg lög á hljóðfæri til að komast áfram.');
INSERT INTO front_page_products(name, price, category_id, on_sale, description) VALUES('Star fox 64',6000,  1, false, 'Star Fox er geimskotleikur sem er búinn til af Nintendo árið 1993. Leikurinn fylgir bardagahópi sem kallast Star Fox, undir forystu aðalpersónunnar Fox McCloud. Leikurinn felur í sér ævintýri um Lylat plánetukerfið í bardagaflugvélunum, á fæti sem og öðrum farartækjum.');
INSERT INTO front_page_products(name, price, category_id, on_sale, description) VALUES('Pokemon Pikachu Yellow',8000, 2, false, 'Pokemon Pikachu Yellow er sérstök útgáfa af Pokemon Red og Pokemon Blue útgáfunum. Tekin var formúlan frá leikjum sem slógu í geng og við það var bætt allskyns nýjum fítusum. Þetta er 100% ekta Nintendo leikur. Original gameboy leiki er hægt að spila á GameBoy, Game Boy Color og Game Boy Advance kerfi.');
INSERT INTO front_page_products(name, price, category_id, on_sale, description) VALUES('Tetris',3000, 2, false, 'Tetris er pússlleikur fyrir Gameboy. Leikurinn var fyrst gefinn út árið 1989. Leikurinn er aðeins studdur af upprunalegum Nintendo GameyBoy leikajtölvum. Original gameboy leiki er hægt að spila á GameBoy, Game Boy Color og Game Boy Advance kerfi.');
INSERT INTO front_page_products(name, price, category_id, on_sale, description) VALUES('Super Mario Land 3 Wario Land',5000, 2, false, 'Super Mario Land 3 Wario Land er leikur sem er þróaður af Nintendo fyrir GameBoy. Leikurinn var fyrst gefinn út árið 1994. Original gameboy leiki er hægt að spila á GameBoy, Game Boy Color og Game Boy Advance kerfi.');
INSERT INTO front_page_products(name, price, category_id, on_sale, description) VALUES('Pac-Man',1000, 2, false, 'Pac-Man er hungruð vera. Markmið leiksins er að borða allar doppurnar af skjánum en draugar koma geta staðið í vegi fyrir Pac-man. Original gameboy leiki er hægt að spila á GameBoy, Game Boy Color og Game Boy Advance kerfi.');
INSERT INTO front_page_products(name, price, category_id, on_sale, description) VALUES('Mega Man X4',2000, 3, false, 'Fjórði leikurinn í Mega Man X seríunni. Hann kom út árið 1997 en á sér stað á 22. öld í samfélagi byggðu af mönnum og gáfuðum vélmennum. Þetta er hasarleikur þar sem spilarinn þarf að ljúka átta stigum í hvaða röð sem er meðan hann berst við óvini, öðlast “power-ups” og vinnur sérstök vopn. Ólíkt fyrri leikjum í seríunni, þá leyfir Mega Man X4 spilaranum að velja á milli söguhetjanna tveggja í upphafi leiks: X, sem notar hefðbundnar, langdrægar árásir; eða Zero, sem fer með skammdræga sverð. Allir leikirnir frá Sony PlayStation 1 virka á PlayStation1.');
INSERT INTO front_page_products(name, price, category_id, on_sale, description) VALUES('Driver 2',1200, 3, false, 'Driver 2 frá PlayStation var gefinn út árið 2000. Í leiknum setur spilarinn sig í spor Tanners sem er leynilögregla. Í Driver 2 eru fjórar borgir; Chicago, Havana, Las Vegas og Rio de Janeiro. Þar er hægt að fara útúr bílnum og stela öðrum sem var ekki hægt í fyrsta Driver leiknum. Allir leikirnir frá Sony PlayStation 1 virka á PlayStation 1.');
INSERT INTO front_page_products(name, price, category_id, on_sale, description) VALUES('Digimon World 3',5000, 3, false, 'Digimon world 3 var gefinn út árið 2002 og er þriðji leikurinn í Digimon heiminum. Þetta er hlutverkaleikur sem segir sögu Junior. Junior byrjar að leika MMORPG sem heitir „Digimon Online“ með vinum sínum, en þegar hryðjuverkamenn ráðast á þá eru Junior og hinir leikmennirnir fastir í leiknum og verða að finna leið út með því að nota Digimon félaga sína. Allir leikirnir frá Sony PlayStation 1 virka á PlayStation 1.');
INSERT INTO front_page_products(name, price, category_id, on_sale, description) VALUES('Tetris plus',1300, 3, false, 'Tetris plus er púsli- tölvuleikur, upprunalega framleiddur af Natsume og gefinn út af Jaleco fyrir spilakassa árið 1996. Sama ár var hann fluttur á Game Boy, Sega Saturn og PlayStation. Leikurinn samanstendur af tveimur hömum; klassískt og púsl. Markmið leikmannsins er að leiðbeina kubbum sem birtast efst á síðunni. Ef kubburinn lendir á skarði sem er nógu stórt til að hann passi í gegn, dettur hann niður á kubbana fyrir neðan en ef skarðið er ekki nógu stórt, fellur hann ofan á kubbana og stækkar kubbaturninn. Ef kubbarnir ná toppnum tapar leikmaðurinn.Allir leikirnir frá Sony PlayStation 1 virka á PlayStation1.');
INSERT INTO front_page_products(name, price, category_id, on_sale, description) VALUES('Battlefield 3',1300, 4, false, 'Battlefield 3 er hasarleikur sem gerir spilurum kleift að sökkva sér inn í umhverfi leiksins bæði andlega og líkamlega betur en nokkru sinni fyrr. Allir Xbox 360 leikir eru gerðir fyrir og spilaðir á Xbox 360 kerfi.');
INSERT INTO front_page_products(name, price, category_id, on_sale, description) VALUES('Resident Evil 6',2000, 4, false, 'Resident Evil 6 er skotleikur sem er spilaður í þriðu persónu. Leikurinn er þróaður og gefinn út af Capcom. Allir Xbox 360 leikir eru gerðir fyrir og spilaðir á Xbox 360 kerfi.');
INSERT INTO front_page_products(name, price, category_id, on_sale, description) VALUES('Forza 2',1000, 4, false, 'Forza 2 er kappakstursleikur sem er spilaður á Xbox 360. Leikurinn var gefinn út árið 2005. Spilarar keppa í keppnum út um allan heim og nota alvöru bíla. Allir Xbox 360 leikir eru gerðir fyrir og spilaðir á Xbox 360 kerfi.');
INSERT INTO front_page_products(name, price, category_id, on_sale, description) VALUES('NHL 10',500, 4, false, 'NHL 10 er íshokkí leikur sem spilaður er í fyrstu persónu. Hægt er að keppa í einn á móti einum. Leikurinn styður allt að 2-7 spilara í einu. Allir Xbox 360 leikir eru gerðir fyrir og spilaðir á Xbox 360 kerfi.');
